package hello.jdbc.service;import java.sql.Connection;import java.sql.SQLException;import org.springframework.transaction.PlatformTransactionManager;import org.springframework.transaction.TransactionStatus;import org.springframework.transaction.support.DefaultTransactionDefinition;import hello.jdbc.domain.Member;import hello.jdbc.repository.MemberRepositoryV3;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;/** * 트랜잭션 - 트랜잭션 매니저 */@RequiredArgsConstructor@Slf4jpublic class MemberServiceV3_1 {	private final PlatformTransactionManager transactionManager;	private final MemberRepositoryV3 repository;	public void accountTransfer(String fromId, String toId, int money) throws SQLException {		TransactionStatus status = transactionManager.getTransaction(new DefaultTransactionDefinition());		try {			bizLogic(fromId, toId, money);			transactionManager.commit(status);		} catch (Exception e) {			transactionManager.rollback(status);			throw new IllegalStateException(e);		}	}	private void release(Connection conn) {		if (conn != null) {			try {				conn.setAutoCommit(true);				conn.close();			} catch (Exception e) {				log.error("error", e);			}		}	}	private void bizLogic( String fromId, String toId, int money) throws SQLException {		Member fromMember = repository.findById(fromId);		Member toMember = repository.findById(toId);		repository.update(fromMember.getMemberId(), fromMember.getMoney() - money);		validation(toMember);		repository.update(toMember.getMemberId(), toMember.getMoney() + money);	}	private void validation(Member toMember) {		if ("ex".equals(toMember.getMemberId())) {			throw new IllegalStateException("이체중 예외 발생");		}	}}