package hello.jdbc.service;import java.sql.SQLException;import org.springframework.transaction.PlatformTransactionManager;import org.springframework.transaction.support.TransactionTemplate;import hello.jdbc.domain.Member;import hello.jdbc.repository.MemberRepositoryV3;import lombok.extern.slf4j.Slf4j;/** * 트랜잭션 - 트랜잭션 템플릿 */@Slf4jpublic class MemberServiceV3_2 {	private final TransactionTemplate txTemplate;	private final MemberRepositoryV3 repository;	public MemberServiceV3_2(PlatformTransactionManager txManager, MemberRepositoryV3 repository) {		this.txTemplate = new TransactionTemplate(txManager);		this.repository = repository;	}	public void accountTransfer(String fromId, String toId, int money) throws SQLException {		txTemplate.executeWithoutResult((status) -> {			try {				bizLogic(fromId, toId, money);			} catch (SQLException e) {				throw new IllegalStateException(e);			}		});	}	private void bizLogic( String fromId, String toId, int money) throws SQLException {		Member fromMember = repository.findById(fromId);		Member toMember = repository.findById(toId);		repository.update(fromMember.getMemberId(), fromMember.getMoney() - money);		validation(toMember);		repository.update(toMember.getMemberId(), toMember.getMoney() + money);	}	private void validation(Member toMember) {		if ("ex".equals(toMember.getMemberId())) {			throw new IllegalStateException("이체중 예외 발생");		}	}}