package hello.jdbc.service;import java.sql.SQLException;import org.springframework.transaction.annotation.Transactional;import hello.jdbc.domain.Member;import hello.jdbc.repository.MemberRepositoryV3;import hello.jdbc.repository.MemberRepositoryV4_2;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;/** * 트랜잭션 - 트랜잭션 템플릿 */@RequiredArgsConstructor@Slf4jpublic class MemberServiceV4 {	private final MemberRepositoryV4_2 repository;	@Transactional	public void accountTransfer(String fromId, String toId, int money){		bizLogic(fromId, toId, money);	}	private void bizLogic( String fromId, String toId, int money){		Member fromMember = repository.findById(fromId);		Member toMember = repository.findById(toId);		repository.update(fromMember.getMemberId(), fromMember.getMoney() - money);		validation(toMember);		repository.update(toMember.getMemberId(), toMember.getMoney() + money);	}	private void validation(Member toMember) {		if ("ex".equals(toMember.getMemberId())) {			throw new IllegalStateException("이체중 예외 발생");		}	}}