package hello.jdbc.connection;import static hello.jdbc.connection.ConnectionConst.*;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;import javax.sql.DataSource;import org.junit.jupiter.api.Test;import org.springframework.jdbc.datasource.DriverManagerDataSource;import com.zaxxer.hikari.HikariDataSource;import lombok.extern.slf4j.Slf4j;@Slf4jpublic class ConnectionTest {	@Test	void driverManger() throws SQLException {		Connection conn1 = DriverManager.getConnection(URL, USERNAME, PASSWORD);		Connection conn2 = DriverManager.getConnection(URL, USERNAME, PASSWORD);		log.info("conn={}, class={}",conn1, conn1.getClass());		log.info("conn={}, class={}",conn2, conn2.getClass());	}	@Test	void driverManagerDatasource() throws SQLException {		DriverManagerDataSource datasource = new DriverManagerDataSource(URL, USERNAME, PASSWORD);		useDatasource(datasource);	}	@Test	void hikariDatasource() throws SQLException, InterruptedException {		HikariDataSource datasource = new HikariDataSource();		datasource.setJdbcUrl(URL);		datasource.setUsername(USERNAME);		datasource.setPassword(PASSWORD);		useDatasource(datasource);		Thread.sleep(1000l);	}	private void useDatasource(DataSource datasource) throws SQLException {		Connection conn1 = datasource.getConnection();		Connection conn2 = datasource.getConnection();		log.info("conn={}, class={}",conn1, conn1.getClass());		log.info("conn={}, class={}",conn2, conn2.getClass());	}}